/*
* This Small script restructures the UBER menu DOM to cator for the desired UI design
* Without this script the DOM generated by UBER menu's content content feature
* is not structured in appropriately for the desired UI we have gone for
* */

(function($){
    var contentBlock = $('.ubermenu-content-block');
    var targetParent = contentBlock.siblings('.ubermenu-target');
    contentBlock.each(function(index){
       targetParent.eq(index).append(contentBlock.eq(index));
    });
})(jQuery);